distinct orange_product_param_key as "orangeProductParamKey",
orange_product_id as "productId"
from tuya_iot.orange_product_param2v where 1=1 

    and orange_product_id = #{productId,jdbcType=BIGINT}



    and orange_product_param_key = #{productParamKey,jdbcType=VARCHAR}
